  };
  return (
    <div className="todoContainer">
      <input placeholder="Input your TOdo" onChange={itemEvent}></input>
      <button onClick={listOfItems}>Add your Todo</button>
      <ul>
        <li>{inputlist}</li>
      </ul>
    </div>
  );
}
