import React, { useState } from "react";
import TodoLists from "./lists";
export default function Todolist() {
  const [items, setitems] = useState([]);
  const [inputlist, setinputlist] = useState("");

  const RemoveBtn = (id) => {
    console.log("deleted");

    setitems((oldItems) => {
      return oldItems.filter((ALists, index) => {
        return index !== id;
      });
    });
  };

  const Addbtn = () => {
    if (inputlist !== " ") {
      setitems((oldItems) => {
        return [...oldItems, inputlist];
      });
      setinputlist(" ");
    }
  };
  const onchangetext = (e) => {
    setinputlist(e.target.value);
  };
  return (
    <div className="todoContainer">
      <input
        value={inputlist}
        placeholder="Input your TOdo"
        onChange={onchangetext}
      ></input>
      <button onClick={Addbtn}>Add your Todo</button>
      <ul>
        {items.map((itemsList, index) => {
          return (
            <TodoLists
              key={index}
              lists={itemsList}
              RemoveBtn={RemoveBtn}
              id={index}
            />
          );
        })}
      </ul>
    </div>
  );
}
